import React, { useState, useContext, useEffect } from "react";
import Calendar from "./Calendar";

import dateContext from "../../context/dates/dateContext";
import barberContext from "../../context/barbers/barberContext";

import { Button, Modal } from "react-bootstrap";

const SummaryModal = ({ show, handleClose, service }) => {
  const datesContext = useContext(dateContext);
  const {
    horas,
    horasBarbero,
    diaSeleccionado,
    mesSeleccionado,
    idSeleccionado,
    getDates,
    getHorasBarbero,
  } = datesContext;

  const barbersContext = useContext(barberContext);
  const { barberos, getBarbers } = barbersContext;

  //traer las horas disponibles
  useEffect(() => {
    getDates();
    getBarbers();
  }, []);

  const handleChange = (e) => {
    console.log(e.target.value);
    getHorasBarbero(e.target.value);
  };

  return (
    <>
      <Modal show={show} onHide={handleClose} size="xl">
        <Modal.Header closeButton>
          <Modal.Title>Ingrese Sus datos para confirmar la Reserva</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="row">
            <div className="col-md-12">
              <div className="card">
                <div className="card-body">
                  {service.nombre}
                  Valor: {service.precio}
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-4 ">
              <h4 className="p-3">Barber@s</h4>
              <div className="card">
                <div className="card-header">Nombre 1</div>
                <div className="card-body">
                  <h6>Hola</h6>
                </div>
              </div>
            </div>
            <div className="col-md-3 p-3"></div>

            <div className="col-md-5 p-3  "></div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleClose}>
            Reservar
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SummaryModal;
