import React, { useReducer } from "react";

import dateContext from "./dateContext";
import dateReducer from "./dateReducer";

import { GET_DATES, SET_MONTH, SET_DAY } from "../../types/index";

const DateState = (props) => {
  const horas = [
    { idEstilista: 1, dia: 5, horaInicial: 10, disponible: true },
    { idEstilista: 1, dia: 5, horaInicial: 11, disponible: true },
    { idEstilista: 1, dia: 5, horaInicial: 12, disponible: false },
    { idEstilista: 2, dia: 3, horaInicial: 9, disponible: true },
    { idEstilista: 3, dia: 22, horaInicial: 10, disponible: true },
  ];

  const initialState = {
    horas: [],
    diaSeleccionado: null,
    mesSeleccionado: null,
    horaSeleccionada: null,
  };

  const [state, dispatch] = useReducer(dateReducer, initialState);

  const getDates = () => {
    dispatch({ type: GET_DATES, payload: horas });
  };

  const setDay = (day) => {
    dispatch({ type: SET_DAY, payload: day });
  };

  const setMonth = (month) => {
    dispatch({ type: SET_MONTH, payload: month });
  };

  return (
    <dateContext.Provider
      value={{
        horas: state.horas,
        diaSeleccionado: state.diaSeleccionado,
        mesSeleccionado: state.mesSeleccionado,
        horaSeleccionada: state.horaSeleccionada,
        getDates,
        setDay,
        setMonth,
      }}
    >
      {props.children}
    </dateContext.Provider>
  );
};

export default DateState;
