import React, { useState, useContext } from "react";
import { Button } from "react-bootstrap";
import dateContext from "../../context/dates/dateContext";

const DateButton = (props) => {
  const datesContext = useContext(dateContext);
  const { setCurrentDate } = datesContext;

  const [clicked, setClicked] = useState(false);

  const handleClickDate = (e) => {
    setClicked(!clicked);
    if (clicked) {
      setCurrentDate(parseInt(e.target.value));
    } else {
      setCurrentDate(null);
    }
  };

  const color = clicked ? "success" : "primary";

  return (
    <Button
      variant={color}
      className="m-1"
      value={props.hora.id_hora}
      disabled={props.seleccionado}
      onClick={handleClickDate}
    >
      {props.hora.hora_inicial}:00 - {props.hora.hora_inicial + 1}:00
    </Button>
  );
};

export default DateButton;
