import { GET_DATES, SET_MONTH, SET_DAY, CURRENT_DAY } from "../../types";

export default (state, action) => {
  switch (action.type) {
    case GET_DATES:
      return { ...state, horas: action.payload };

    case SET_MONTH:
      return { ...state, mesSeleccionado: action.payload };

    case SET_DAY:
      return { ...state, diaSeleccionado: action.payload };

    case CURRENT_DAY:
      return {
        ...state,
        horasDia: state.horas.filter(
          (hora) =>
            hora.dia === action.payload.dia && hora.mes === action.payload.mes
        ),
      };

    default:
      return state;
  }
};
