import React, { useState, useContext, useEffect } from "react";
import Calendar from "./Calendar";

import dateContext from "../../context/dates/dateContext";
import barberContext from "../../context/barbers/barberContext";

import { Button, Modal } from "react-bootstrap";

const BookingModal = ({ show, handleClose, service }) => {
  const datesContext = useContext(dateContext);
  const { diaSeleccionado, mesSeleccionado, horasDia, getDates } = datesContext;

  const barbersContext = useContext(barberContext);
  const { barberos, getBarbers } = barbersContext;

  //traer las horas disponibles
  useEffect(() => {
    getDates();
    getBarbers();
  }, []);

  return (
    <>
      <Modal show={show} onHide={handleClose} size="xl">
        <Modal.Header closeButton>
          <Modal.Title>Reserva de Hora</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="row">
            <div className="col-md-12">
              <div className="card">
                <div className="card-body">
                  {service.nombre}
                  Valor: {service.precio}
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-4 ">
              <h4 className="p-3">Barber@s</h4>
              <div className="card">
                <div className="card-header">Nombre 1</div>
                <div className="card-body">
                  <h6>Hola</h6>
                  <select
                    className="form-select"
                    aria-label="Default select example"
                  >
                    <option selected>Seleccione Barbero</option>
                    {barberos.map((barbero) => (
                      <option value={barbero.id} key={barbero.id}>
                        {barbero.nombre}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
            <div className="col-md-3 p-3">
              <h4>Seleccione El DÃ­a</h4>
              <Calendar />
            </div>

            <div className="col-md-5 p-3  ">
              <h4>Seleccione La hora</h4>
              {horasDia.map((hora) => (
                <button
                  type="button"
                  className="btn btn-success m-1"
                  key={hora.id}
                >
                  {hora.horaInicial}:00 - {hora.horaInicial + 1}:00
                </button>
              ))}
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleClose}>
            Siguiente Paso
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default BookingModal;
